var Particle=function(t){this.acceleration=createVector(0,.05),this.velocity=createVector(random(-3,3),random(-1,0)),this.position=t.copy(),this.lifespan=255};Particle.prototype.run=function(){this.update(),this.display()},Particle.prototype.update=function(){this.velocity.add(this.acceleration),this.position.add(this.velocity),this.lifespan-=2},Particle.prototype.display=function(){c=color(0,0,random(200,255),this.lifespan),fill(c),ellipse(this.position.x,this.position.y,12,12)},Particle.prototype.isDead=function(){return this.lifespan<0?!0:!1};var ParticleSystem=function(t){this.origin=t.copy(),this.particles=[]};ParticleSystem.prototype.addParticle=function(){this.particles.push(new Particle(this.origin))},ParticleSystem.prototype.run=function(){for(var t=this.particles.length-1;t>=0;t--){var i=this.particles[t];i.run(),i.isDead()&&this.particles.splice(t,1)}};